for(var field=document.querySelector(".field"),cells=document.querySelectorAll(".cell"),i=0;i<cells.length;i++)cells[i].addEventListener("click",function(t){t.target.classList.contains("empty")&&(field.classList.contains("x-turn")?t.target.classList.add("x"):t.target.classList.add("o"),t.target.classList.remove("empty"),field.classList.toggle("x-turn"),field.classList.toggle("o-turn"))});